  * Given a number n, return the sum of all multiples of n less than or equal to n
  *
  * @param {number} n
  * @param {number} m
  * @returns {number}
  *
  * @example
  *
  * getSumOfMultiples(7, 2)
  * // => 6
  *
  * getSumOfMultiples(7, 5)
  * // => 14
  */
const getSumOfMultiples = (n, m) => {
  if(n <= 1) {
    return 0;
  }

  let sum = n;
  for(let i = n + 1; i <= n * m; i += n) {
    sum += i;
  }

  return sum;
};

