  */
const fixSpaces = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/\s/g, "_");
  }

  let lastWord = words[wordsLength - 1];
  if(lastWord.length > 1){
    lastWord = lastWord.replace(/\s+/g, " ");
  }

  return words.join(" ") + " " + lastWord;
}

const replaceSpaces = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/\s/g, "_");
  }

  return words.join(" ");
}

const convertToTitleCase = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/\s/g, "_");
    words[i] = words[i].charAt(0).toUpperCase() + words[i].slice(1);
  }

  return words.join(" ");
}

const removeAllSpaces = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/\s/g, "");
  }

  return words.join(" ");
}

const stripAccents = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/[-]/g, "");
  }

  return words.join(" ");
}

const getTextLength = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/\s/g, "");
  }

  return words.join(" ").length;
}

const getNumberOfWords = (text) => {
  let words = text.split(" ");
  let wordsLength = words.length;

  for(let i = 0; i < wordsLength; i++){
    words[i] = words[i].replace(/\s/g, "");
  }

  return words.length;
}

module.exports = {
