  */
const parseNestedParens = (paren_string) => {
  const stack = []
  const level = 0
  const level_stack = []
  const level_max = []
  paren_string.split(' ').forEach((item) => {
    stack.push(item)
    if (item === '(') {
      level_stack.push(level)
      level += 1
    }
    if (item === ')') {
      level_stack.pop()
      level -= 1
    }
  })

  if (level_stack.length === 0) {
    return []
  }

  level_max.push(level_stack[level_stack.length - 1])
  const last_element = stack.pop()

  while (level_stack.length > 0) {
    const current_level = level_stack[level_stack.length - 1]
    const last_element_next = stack.pop()
    if (last_element === '(') {
      level_stack.push(current_level + 1)
    }
    if (last_element === ')') {
      level_stack.pop()
    }
    if (last_element_next === '(') {
      level_stack.push(current_level + 1)
    }
    if (last_element_next === ')') {
      level_stack.pop()
    }
    level_max.push(level_stack[level_stack.length - 1])
    last_element = last_element_next
  }
  return level_max
}

