/*
  You'll be given a string of words, a nd yo ur task is to count the number
  of boredoms. A boredom is a sentence th at st arts wi t h the w ord "I".  Sentences are delimited by  '. ', '?' or '!'. 

  For exam ple:
  >>> isBored("Hello world")
  0
  >>> isBored("The sky is blue. The sun is shining. I love this weather")
  1
  */
const isBored = (S) => {
  let numBoredom = 0;
  let str = S.toLowerCase();
  let allBoredoms = new Set(str.split(" ").filter(s => s.indexOf('i') > -1));
  let lastWord = str.split(" ").pop().toLowerCase();
  let prevWord = lastWord;
  let prevStr = str.split(" ").pop().toLowerCase();
  if(lastWord === 'i'){
    numBoredom++;
  }
  for(let i = 0; i < str.length; i++){
    let word = str.split(" ")[i].toLowerCase();
    if(word === prevWord){
      if(prevStr.indexOf('i') > -1){
        numBoredom++;
      }
    }
    else if(word === 'i'){
      numBoredom++;
    }
    else{
      if(allBoredoms.has(word)){
        numBoredom++;
      }
    }
    prevWord = word;
    prevStr = str.split(" ")[i].toLowerCase();
  }
  return numBoredom;
}

module.exports = isBored;<|endoftext|>

