  >>> prodSigns([0, 1]) == 0
  >>> prodSigns([]) == null
  */
const prodSigns = (arr) => {
  let count = 0
  let sign = 0
  let sum = 0
  for (let i = 0; i < arr.length; i++) {
    count += arr[i]
    if (arr[i] === 1) {
      sign = 1
    } else if (arr[i] === -1) {
      sign = -1
    }
    sum += arr[i] * Math.pow(sign, count)
  }
  return sum
}

