  ['()', '(())', '(()())']
  */
const separateParenGroups = (paren_string) => {
  let stack = []
  let open_braces = 0
  let braces_used = 0
  let result = []
  for (let i = 0; i < paren_string.length; i++) {
    let c = paren_string[i]
    if (c === '(') {
      open_braces += 1
      stack.push(c)
    } else if (c === ')') {
      open_braces -= 1
      if (open_braces <= 0) {
        if (stack.length === 0) {
          stack = []
          open_braces = 0
        } else {
          let last_open_brace = stack.pop()
          braces_used += 1
          if (last_open_brace === '(') {
