  E.g. (()()) ha s maximum two levels of ne sting while((())) hast hree.

  >>> parseNestedParens('(()()) ((())) () ((())()())')
  [2, 3, 1, 3]
  */
const parseNestedParens = (paren_string) => {
  const stack = [];
  const nested_p = paren_string.split(' ');
  const last_nested_paren = nested_p[nested_p.length - 1];
  const is_last_nested_paren = nested_p.indexOf(last_nested_paren) === nested_p.length - 1;

  for (let i = 0; i < nested_p.length; i++) {
    const group = nested_p[i];
    const is_group_open = group.startsWith('(');
    const is_group_close = group.startsWith(')');

    if (is_group_open) {
      stack.push(i);
    } else if (is_group_close) {
      stack.pop();
    }

    if (stack.length === 0) {
      // No more groups, so we're done.
      if (is_last_nested_paren) {
        return nested_p.slice(0, -1).join(' ');
      }
      return nested_p.slice(0, stack.length).join(' ');
    }
  }

  return nested_p;
};

