  >>> median([3, 1, 2, 4, 5])
  3
  >>> median([-10, 4, 6, 1000, 10, 20])
  8.0
  */
const median = (l) => {
  const sortedList = sorted(l);
  if (sortedList.length % 2 === 0) {
    const half = Math.floor(sortedList.length / 2);
    return (sortedList[half - 1] + sortedList[half]) / 2;
  } else {
    return sortedList[sortedList.length / 2];
  }
};

/**
  *  Returns the median of elements in the list  l.
  *  Returns NaN if l is empty.
  *  
  *  @param {list} l 
  *  @return {number}
  */
const median2 = (l) => {
  if (l.length === 0) {
    return NaN;
  }
  return sorted(l)[Math.floor(l.length / 2)];
};

module.exports = {
  median,
  median2
};
