  const re = /\s+/g;
  const words = s.split(re);
  const new_words = [];

  for (let i = 0; i < words.length; i += 1) {
    if (i === words.length - 1) {
      new_words.push(words[i]);
    } else {
      let j = i + 1;
      while (j < words.length) {
        if (words[i].length!== words[j].length) {
          break;
        }
        new_words.push(words[i] + words[j]);
        j += 1;
      }
      new_words.push(words[i]);
    }
  }
  return new_words.join('');
};

