  const digRes = new Array(numDigits);
  for (let i = 0; i < numDigits; i++) {
    digRes[i] = res % 10;
    res = Math.floor(res / 10);
    carry += res;
  }
  for (let i = numDigits - 1; i >= 0; i--) {
    carry += (carry >= 10)? 0 : carry;
    digRes[i] += carry;
  }

  let resStr = '';
  if (isNeg) {
    resStr = '-' + digRes.join('');
  } else {
    resStr = digRes.join('');
  }

  return resStr;
};

