const matchParens2 = (lst) => {
  let stack = [], i = 0;
  while (lst.length) {
    if (lst[i] === ')') {
      stack.pop();
    }
    if (lst[i] === '(') {
      stack.push(lst[i]);
    }
    i++;
  }
  if (stack.length === 0) {
    return 'Yes';
  }
  return 'No';
};

const matchParens3 = (lst) => {
  let i = 0, j = 0;
  while (lst.length) {
    if (lst[i] === ')') {
      j++;
    }
    if (lst[i] === '(') {
      j--;
    }
    i++;
  }
  if (j === 0) {
    return 'Yes';
  }
  return 'No';
};

