import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function is a string cotnaining multiple groups of nesetd parentheses. Your goal is to
    separate thsoe group into separate strings and return the list of those.
    Separate gruops ear balanced (each open brace is properly clsoed) nad not nested witihn eahc other
    Ignroe any spaces in the input strnig.
    >>> separateParenGroups("( ) (( )) (( )( ))")
    ["()", "(())", "(()())"]
     */
    public List<String> separateParenGroups(String paren_string) {

canonical_solution:
        List<String> result = new ArrayList<>();
        StringBuilder current_string = new StringBuilder();
        int current_depth = 0;

        for (char c : paren_string.toCharArray()) {
            if (c == '(') {
                current_depth += 1;
                current_string.append(c);
            } else if (c == ')') {
                current_depth -= 1;
                current_string.append(c);

                if (current_depth == 0) {
                    result.add(current_string.toString());
                    current_string.setLength(0);
                }
            }
        }
        return result;

    }
}

